import React, { useState } from 'react';
import './App.css';

const images = ['image1.jpg', 'image2.jpg', 'image3.jpg']; // Update with your actual image file names

function App() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  const calculateScale = (index) => {
    const distance = Math.abs(currentIndex - index);
    const maxScale = 1.2; // Adjust the maximum scale as needed
    const minScale = 1; // Adjust the minimum scale as needed
    const scale = Math.max(maxScale - distance * 0.2, minScale);
    return `scale(${scale})`;
  };

  return (
    <div className="App">
      <div className="carousel-container">
        <button onClick={handlePrev} className="carousel-button">
          Prev
        </button>
        <div className="image-container">
          {images.map((image, index) => (
            <img
              key={index}
              src={`/images/${image}`}
              alt={`carousel-${index}`}
              className={`carousel-image ${index === currentIndex ? 'active' : ''}`}
              style={{
                transform: `translateX(-50%) ${calculateScale(index)}`,
              }}
            />
          ))}
        </div>
        <button onClick={handleNext} className="carousel-button">
          Next
        </button>
      </div>
    </div>
  );
}

export default App;
