import React, { useState, useRef, useEffect } from 'react';

const Carousel = ({ items }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const carouselRef = useRef(null);

  const handleScroll = () => {
    const container = carouselRef.current;
    const scrollLeft = container.scrollLeft;
    const itemWidth = container.clientWidth;

    // Check if the user has scrolled to the end of the current item
    if (scrollLeft % itemWidth === 0) {
      const newIndex = Math.floor(scrollLeft / itemWidth);

      // Update the current index
      setCurrentIndex(newIndex);
    }
  };

  // Reset the scroll position when reaching the beginning or end
  useEffect(() => {
    const container = carouselRef.current;
    const itemWidth = container.clientWidth;

    // Scroll to the first item when reaching the end
    if (currentIndex === items.length) {
      container.scrollLeft = 0;
      setCurrentIndex(0);
    }

    // Scroll to the last item when reaching the beginning
    if (currentIndex === -1) {
      container.scrollLeft = itemWidth * (items.length - 1);
      setCurrentIndex(items.length - 1);
    }
  }, [currentIndex, items.length]);

  return (
    <div
      className="carousel-container"
      ref={carouselRef}
      onScroll={handleScroll}
    >
      {items.map((item, index) => (
        <div key={index} className="carousel-item">
          {item}
        </div>
      ))}
    </div>
  );
};

export default Carousel;
