import React, { useState } from 'react';
import './App.css';

const images = ['image1.jpg', 'image2.jpg', 'image3.jpg', 'image4.jpg', 'image5.jpg', 'image6.jpg']; // Update with your actual image file names

function App() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="App">
      <div className="carousel-container">
        <img
          src={`/images/${images[(currentIndex + 2) % images.length]}`}
          alt={`carousel-${(currentIndex + 2) % images.length}`}
          className="carousel-image"
        />
        <img
          src={`/images/${images[currentIndex]}`}
          alt={`carousel-${currentIndex}`}
          className="carousel-image"
        />
        <img
          src={`/images/${images[(currentIndex + 1) % images.length]}`}
          alt={`carousel-${(currentIndex + 1) % images.length}`}
          className="carousel-image"
        />
        <button onClick={handlePrev} className="carousel-button">
          Prev
        </button>
        <button onClick={handleNext} className="carousel-button">
          Next
        </button>
      </div>
    </div>
  );
}

export default App;
