import React, { useState } from 'react';
import './App.css';

const images = ['image1.jpg', 'image2.jpg', 'image3.jpg']; // Update with your actual image file names

function App() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="App">
      <div className="carousel-container">
        <button onClick={handlePrev} className="carousel-button">
          Prev
        </button>
        <div className="carousel-wrapper">
          <div className="carousel-preview" style={{ backgroundImage: `url(${images[currentIndex]})` }} />
          <img
            src={images[currentIndex]}
            alt={`carousel-${currentIndex}`}
            className="carousel-image"
          />
          <div className="carousel-preview" style={{ backgroundImage: `url(${images[(currentIndex + 1) % images.length]})` }} />
        </div>
        <button onClick={handleNext} className="carousel-button">
          Next
        </button>
      </div>
    </div>
  );
}

export default App;
